sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Callback function defined in getReference() call
    g_form.getReference('u_requested_for', function(appendDesc) {
        g_form.setValue('description', g_form.getValue('description') + '\n' + 'New short description: ' + newValue + '\n' + appendDesc.manager.user_name);
    });

    //Callback function outside of getReference call 
    /*
	var userRecord = g_form.getReference('u_requested_for', appendDesc);
        function appendDesc(userRecord) {
            if (userRecord != '') {
                var currentDesc = g_form.getValue('description');
    			g_form.setValue('description', currentDesc + '\n' + 'New short description: ' + newValue + '\n' + userRecord.user_name);
            }
        }
    */

}
