sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var previousShortDesc = oldValue;
    var newShortDesc = g_form.getValue('short_description');

    var ga = new GlideAjax('UpdateNeeditDescription');
    ga.addParam('sysparm_name', 'getUserInfo');
    ga.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
    ga.getXML(userRecord);

    function userRecord(response) {
        var answer = JSON.parse(response.responseXML.documentElement.getAttribute("answer"));
		if (answer) {
			//var desc = previousShortDesc + '\n' +  newShortDesc + '\n' + answer[0].user_name + '\n' + answer[0].email;
			var desc = previousShortDesc + '\n' +  newShortDesc + '\n' + answer.user_name + '\n' + answer.email;
			g_form.setValue('description',desc);
		}
    }
}
