sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    var ga = new GlideAjax('NeeditDisplayProperties');
    ga.addParam('sysparm_name', 'getProperties');
    ga.addParam('sysparm_userID', g_form.getValue(newValue));
    ga.getXML(getUserProperties);

    function getUserProperties(response) {
        var answer = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));
        alert("email addy: " + answer.email + "\nManager: " + answer.manager);
    }

}
