sys_script_include.script:
var RecordlookupREST = Class.create();
RecordlookupREST.prototype = {
    initialize: function() {},
    lookupRecords: function(tableName, limit) {
        var answer = [];
        var recGr = new GlideRecord(tableName);

        if (limit && limit > 0) {
            recGr.setLimit(limit);
        }
        recGr.query();

        while (recGr.next()) {
            var obj = {};
            obj.display_value = recGr.getDisplayValue();
            obj.sys_id = recGr.getUniqueValue();
            answer.push(obj);
        }
		return answer;
    },
    type: 'RecordlookupREST'
};
