sys_script_include.script:
var TestIPREST = Class.create();
TestIPREST.prototype = {
        initialize: function() {},
        getCity: function() {
            var r = new sn_ws.RESTMessageV2('x_58872_needit.IPInfo', 'GetIPInfo');
            r.setStringParameterNoEscape('specified_field', 'org');
            r.setStringParameterNoEscape('ipaddress', '8.8.8.8');

            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            var responseObj = JSON.parse(responseBody);
           return responseObj.city;
		},

            type: 'TestIPREST'
        };
