sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    //Create new record in x_58872_needit table
    var requestBody = request.body.data;
    var u_requested_for = requestBody.requestedFor;
    var u_request_type = requestBody.requestType;
    var u_what_needed = requestBody.whatNeeded;
    var u_when_needed = requestBody.whenNeeded;
    var short_description = requestBody.shortDescription;

    if (!u_requested_for || !u_request_type || !u_what_needed || !u_when_needed) {
        response.setStatus(400);
        response.setBody({
            error: "requestedFor, requestType, whatNeeded, and whenNeeded are required fields."
        });
        return;
    }

    var gr = new GlideRecord('x_58872_needit_needit');
    gr.initialize();
    gr.u_requested_for = u_requested_for;
    gr.u_request_type = u_request_type;
    gr.u_what_needed = u_what_needed;
    gr.u_when_needed = u_when_needed;
    if (requestBody.shortDescription != '') {
        gr.short_description = short_description;
    }
    var needitRecordSysId = gr.insert();
    if (needitRecordSysId) {
        response.setStatus(201);
        response.setBody({
            message: "NeedIT record created successfully",
            needit_number: gr.number,
            sys_id: needitRecordSysId
        });
    } else {
        // Return an error response if the incident creation failed
        response.setStatus(500);
        response.setBody({
            error: "Failed to create NeedIt record"
        });
    }
})(request, response);
